ALTER TABLE `stat_summary_daily`
MODIFY COLUMN `deposit`  bigint(20) NULL DEFAULT 0 COMMENT '充值总额' AFTER `user_have_bet`,
MODIFY COLUMN `withdraw`  bigint(20) NULL DEFAULT 0 COMMENT '提现总额' AFTER `deposit_user`,
MODIFY COLUMN `bet`  bigint(20) NULL DEFAULT 0 COMMENT '投注总额' AFTER `withdraw_user`,
MODIFY COLUMN `bonus`  bigint(20) NULL DEFAULT 0 COMMENT '奖金/中奖金额' AFTER `bet`,
MODIFY COLUMN `fandian`  bigint(20) NULL DEFAULT 0 COMMENT '返点总额' AFTER `bonus`,
MODIFY COLUMN `dataline`  int(20) NULL DEFAULT 0 COMMENT '时间' AFTER `commission`;

ALTER TABLE `stat_user_daily`
MODIFY COLUMN `deposit`  bigint(20) NULL DEFAULT 0 COMMENT '充值总额' AFTER `user_have_bet`,
MODIFY COLUMN `withdraw`  bigint(20) NULL DEFAULT 0 COMMENT '提现总额' AFTER `deposit`,
MODIFY COLUMN `bet`  bigint(20) NULL DEFAULT 0 COMMENT '投注总额' AFTER `withdraw`,
MODIFY COLUMN `fandian`  bigint(20) NULL DEFAULT 0 COMMENT '返点总额' AFTER `bet`,
MODIFY COLUMN `bonus`  bigint(20) NULL DEFAULT 0 COMMENT '奖金/中奖金额' AFTER `fandian`,
MODIFY COLUMN `dataline`  int(20) UNSIGNED NULL DEFAULT 0 COMMENT '时间' AFTER `activity_cost`;


