DROP TABLE IF EXISTS `activity`;
CREATE TABLE `activity` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `main_title` varchar(100) DEFAULT '' COMMENT '主title',
  `main_title_desc` varchar(255) DEFAULT '' COMMENT '主title的描述',
  `rule` text COMMENT '条款',
  `type` tinyint(4) DEFAULT '1' COMMENT '1 存提款 2体育 3娱乐场 4电子游戏 5 彩票',
  `amount` bigint(20) DEFAULT '0' COMMENT '金额',
  `amount_desc` varchar(100) DEFAULT '' COMMENT '金额下面的描述',
  `img` varchar(100) DEFAULT '' COMMENT '图片路径',
  `start_time` timestamp NULL DEFAULT NULL COMMENT '开始时间',
  `end_time` timestamp NULL DEFAULT NULL ON UPDATE CURRENT_TIMESTAMP COMMENT '结束时间',
  `status` tinyint(4) DEFAULT '-1' COMMENT '-1 禁用 1 启用',
  `create_time` timestamp NULL DEFAULT NULL COMMENT '创建时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8 COMMENT='优惠活动表';

